import { useEffect } from "react";
import { useMap } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-markercluster";
import L from "leaflet";

export function SyncZoom({ setZoomLevel, setMapZoom }) {
  const map = useMap();

  useEffect(() => {
    const handleZoom = () => {
      const z = map.getZoom();
      setMapZoom(z);

      if (z < 3) setZoomLevel("continent");
      else if (z < 6) setZoomLevel("country");
      else if (z < 10) setZoomLevel("city");
      else setZoomLevel("city");
    };

    map.on("zoomend", handleZoom);
    handleZoom();

    return () => {
      map.off("zoomend", handleZoom);
    };
  }, [map, setZoomLevel, setMapZoom]);

  return null;
}

export function FitToClusters({ clusterGroupRef }) {
  const map = useMap();

  useEffect(() => {
    if (map && clusterGroupRef.current) {
      const bounds = clusterGroupRef.current.getBounds();
      if (bounds.isValid()) {
        map.fitBounds(bounds, {
          padding: [100, 100],
          animate: true,
        });
      }
    }
  }, [map, clusterGroupRef]);

  return null;
}
