import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import groupByLevel from "../utils/groupByLevel";
import { SyncZoom } from "./SyncZoom";
import GalleryModal from "./GalleryModal";
import { createCheckinIcon } from "../utils/leafletIcon";

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent");
  const [mapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(3);

  const [modalOpen, setModalOpen] = useState(false);
  const [theaterConcerts, setTheaterConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  // Define zoom order and corresponding zoom values
  const zoomOrder = ["continent", "country", "city", "theater"];
  const zoomLevels = { continent: 4, country: 7, city: 12, theater: 18 };

  return (
    <>
      <MapContainer
        worldCopyJump={true}
        center={mapCenter}
        zoom={mapZoom}
        minZoom={3}
        maxZoom={18}
        maxBounds={[
          [-85, -180], // Southwest corner
          [85, 180],   // Northeast corner
        ]}
        style={{ height: "100vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OSM</a>'
        />

        <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />

        {Object.entries(dataToShow).map(([key, items]) => {
          const first = items[0];
          const count = items.length;

          // Skip markers without theater or lat/lng
          if (!first.theater || first.theater.lat == null || first.theater.lng == null) return null;

          return (
            <Marker
              key={key}
              position={[first.theater.lat, first.theater.lng]}
              icon={createCheckinIcon(count)}
              eventHandlers={{
                click: (e) => {
                  const map = e.target._map;

                  const currentIndex = zoomOrder.indexOf(zoomLevel); // use current state directly
                  const nextIndex = Math.min(currentIndex + 1, zoomOrder.length - 1);
                  const nextLevel = zoomOrder[nextIndex];

                  // Fly to the position with the correct zoom
                  map.flyTo([first.theater.lat, first.theater.lng], zoomLevels[nextLevel]);

                  // Open modal if at theater level
                  if (nextLevel === "theater") {
                    setTheaterConcerts(items);
                    setModalOpen(true);
                  }

                  // finally update the zoomLevel state
                  setZoomLevel(nextLevel);
                },
                mouseover: (e) => {
                  // Show popup only at theater level
                  if (zoomLevel === "theater") {
                    const popup = e.target.bindPopup(
                      `<div style="text-align:center; max-width:250px;">
                        ${first.theater.photo ? `<img src="${first.theater.photo}" style="width:100%; border-radius:8px;" />` : ""}
                        <div style="margin-top:5px;">${first.theater.name}</div>
                      </div>`,
                      { maxWidth: 300, minWidth: 200, autoPan: true }
                    );
                    popup.openPopup();
                  }
                },
                mouseout: (e) => {
                  e.target.closePopup();
                },
              }}
            />
          );
        })}
      </MapContainer>

      <GalleryModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        concerts={theaterConcerts}
      />
    </>
  );
}
