import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import groupByLevel from "../utils/groupByLevel";
import { SyncZoom } from "./SyncZoom";
import { createCheckinIcon } from "../utils/leafletIcon";

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent");
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(3);

  const [panelOpen, setPanelOpen] = useState(false);
  const [theaterConcerts, setTheaterConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  return (
    <div className="relative h-screen w-screen flex overflow-hidden">
      {/* Map takes full space, but shrinks when panel opens */}
      <div
        className={`transition-all duration-300 ${
          panelOpen ? "w-[calc(100%-20rem)]" : "w-full"
        }`}
      >
        <MapContainer
          worldCopyJump={true}
          center={mapCenter}
          zoom={mapZoom}
          minZoom={3}
          maxZoom={18}
          maxBounds={[
            [-85, -180],
            [85, 180],
          ]}
          style={{ height: "100vh", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OSM</a>'
          />

          <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />

          {Object.entries(dataToShow).map(([key, items]) => {
            const first = items[0];
            const count = items.length;

            if (!first.theater || first.theater.lat == null || first.theater.lng == null) return null;

            return (
              <Marker
                key={key}
                position={[first.theater.lat, first.theater.lng]}
                icon={createCheckinIcon(count)}
                eventHandlers={{
                  click: (e) => {
                    const map = e.target._map;

                    if (zoomLevel === "continent") {
                      setZoomLevel("country");
                      setMapCenter([first.theater.lat, first.theater.lng]);
                      setMapZoom(3);
                      map.flyTo([first.theater.lat, first.theater.lng], 3);
                    } else if (zoomLevel === "country") {
                      setZoomLevel("city");
                      setMapCenter([first.theater.lat, first.theater.lng]);
                      setMapZoom(6);
                      map.flyTo([first.theater.lat, first.theater.lng], 6);
                    } else if (zoomLevel === "city") {
                      setZoomLevel("theater");
                      setMapCenter([first.theater.lat, first.theater.lng]);
                      setMapZoom(10);
                      map.flyTo([first.theater.lat, first.theater.lng], 10);
                    } else if (zoomLevel === "theater") {
                      setZoomLevel("theaterDetails");
                      setMapCenter([first.theater.lat, first.theater.lng]);
                      setMapZoom(16);
                      map.flyTo([first.theater.lat, first.theater.lng], 18);
                    } else if (zoomLevel === "theaterDetails") {
                      // show panel with concerts
                      const sortedConcerts = [...items].sort(
                        (a, b) => new Date(a.date) - new Date(b.date)
                      );
                      setTheaterConcerts(sortedConcerts);
                      setPanelOpen(sortedConcerts.length > 0);
                    }
                  },
                  mouseover: (e) => {
                    if (zoomLevel === "theaterDetails") {
                      const popup = e.target.bindPopup(
                        `<div style="text-align:center; max-width:250px;">
                          ${
                            first.theater.photo
                              ? `<img src="${first.theater.photo}" style="width:100%; border-radius:8px;" />`
                              : ""
                          }
                          <div style="margin-top:5px;">${first.theater.name}</div>
                        </div>`,
                        { maxWidth: 300, minWidth: 200, autoPan: true }
                      );
                      popup.openPopup();
                    }
                  },
                  mouseout: (e) => {
                    e.target.closePopup();
                  },
                }}
              />
            );
          })}
        </MapContainer>
      </div>

      {/* Sliding panel */}
      <div
        className={`fixed top-0 left-0 h-full w-80 bg-white shadow-xl transform transition-transform duration-300 overflow-y-auto ${
          panelOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-bold">Concerts</h2>
          <button
            className="text-gray-600 hover:text-black"
            onClick={() => setPanelOpen(false)}
          >
            ✕
          </button>
        </div>
        <div className="p-4 space-y-3">
          {theaterConcerts.length === 0 ? (
            <p className="text-gray-500 italic">No concerts found</p>
          ) : (
            theaterConcerts.map((c, idx) => (
              <div
                key={idx}
                className="border rounded-lg p-3 shadow-sm hover:shadow-md transition"
              >
                <h3 className="font-semibold">{c.name}</h3>
                <p className="text-sm text-gray-600">
                  {new Date(c.date).toLocaleDateString()}
                </p>
                <p className="text-sm text-gray-700">
                  {c.artists?.join(", ")}
                </p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
