import { useState } from "react";

export default function TheaterMapWithPanel({ theater, concerts }) {
  const [selectedTheater, setSelectedTheater] = useState(null);

  const handlePinClick = (theaterId) => {
    const theaterConcerts = concerts
      .filter((c) => c.theaterId === theaterId)
      .sort((a, b) => new Date(a.date) - new Date(b.date));

    if (theaterConcerts.length > 0) {
      setSelectedTheater({ id: theaterId, concerts: theaterConcerts });
    } else {
      setSelectedTheater(null); // hide panel if no concert
    }
  };

  return (
    <div className="flex w-full h-screen relative">
      {/* Map container */}
      <div className={`flex-1 transition-all duration-300 ${selectedTheater ? "mr-80" : ""}`}>
        {/* Your Leaflet/Map component here */}
        <div className="w-full h-full bg-blue-200 flex items-center justify-center">
          <button
            className="px-4 py-2 bg-indigo-600 text-white rounded"
            onClick={() => handlePinClick(theater.id)}
          >
            ðŸŽ­ Click Theater Pin
          </button>
        </div>
      </div>

      {/* Sliding panel */}
      <div
        className={`fixed top-0 right-0 h-full w-80 bg-white shadow-2xl transform transition-transform duration-300 overflow-y-auto
          ${selectedTheater ? "translate-x-0" : "translate-x-full"}`}
      >
        {selectedTheater && (
          <div className="p-4">
            <h2 className="text-xl font-bold mb-4">Concerts at {theater.name}</h2>
            <ul className="space-y-3">
              {selectedTheater.concerts.map((concert, idx) => (
                <li key={idx} className="border-b pb-2">
                  <div className="font-medium">{concert.title}</div>
                  <div className="text-sm text-gray-600">
                    {new Date(concert.date).toLocaleDateString()}
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
