import React, { useState, useEffect } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
  useMap,
} from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix default icon path for React + Webpack
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

// Dynamic count-based icon
const createCheckinIcon = (count) =>
  L.divIcon({
    html: `<div style="
      position:relative;
      background-color:#1976d2;
      color:white;
      border-radius:50%;
      width:36px;
      height:36px;
      display:flex;
      justify-content:center;
      align-items:center;
      font-weight:bold;
      font-size:16px;
      border:2px solid white;
    ">${count}</div>`,
    className: "",
    iconSize: [36, 36],
    iconAnchor: [18, 36],
    popupAnchor: [0, -36],
  });

// Group concerts by continent, country, or city
function groupByLevel(concerts, level) {
  const grouped = {};
  (concerts || []).forEach((c) => {
    const key = c[level];
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(c);
  });
  return grouped;
}

// Component to update map view
function ChangeMapView({ center, zoom }) {
  const map = useMap();
  map.setView(center, zoom);
  return null;
}

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent"); // continent → country → city
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(2);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  const nextZoomLevel =
    zoomLevel === "continent"
      ? "country"
      : zoomLevel === "country"
      ? "city"
      : "city";

  const zoomMapping = { continent: 2, country: 4, city: 7 };

  return (
    <MapContainer
      center={mapCenter}
      zoom={mapZoom}
      style={{ height: "100vh", width: "100%" }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />

      {Object.entries(dataToShow).map(([key, items]) => {
        const first = items[0];
        const count = items.length;

        return (
          <Marker
            key={key}
            position={[first.lat, first.lng]}
            icon={createCheckinIcon(count)}
            eventHandlers={{
              click: () => {
                if (zoomLevel !== "city") {
                  setZoomLevel(nextZoomLevel);
                }
                setMapCenter([first.lat, first.lng]);
                setMapZoom(zoomMapping[nextZoomLevel]);
              },
            }}
          >
            <Popup>
              <h3>{key}</h3>
              <p>Total concerts: {count}</p>
              {items.map((c) => (
                <div key={c.name}>
                  <strong>{c.name}</strong> ({c.date})<br />
                  Artists: {c.artists.join(", ")}<br />
                  Ticket: {c.ticket}
                  <br />
                  {c.photos.map((p, i) => (
                    <img
                      key={i}
                      src={p}
                      alt={c.name}
                      width={100}
                      style={{ margin: "2px" }}
                    />
                  ))}
                  <hr />
                </div>
              ))}
            </Popup>
          </Marker>
        );
      })}

      <ChangeMapView center={mapCenter} zoom={mapZoom} />
    </MapContainer>
  );
}
