import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import groupByLevel from "../utils/groupByLevel";
import { SyncZoom } from "./SyncZoom";
import GalleryModal from "./GalleryModal";
import { createCheckinIcon } from "../utils/leafletIcon";

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent");
  const [mapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(3);

  const [modalOpen, setModalOpen] = useState(false);
  const [cityConcerts, setCityConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  return (
    <>
      <MapContainer worldCopyJump={true} center={mapCenter} zoom={mapZoom} minZoom={3}
        maxBounds={[
          [-85, -180], // Southwest corner
          [85, 180],   // Northeast corner
        ]}
        maxZoom={18} style={{ height: "100vh", width: "100%" }}>
        <TileLayer
          url="https://basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OSM</a> &copy; <a href="https://carto.com/">CARTO</a>'
        />

        <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />

        {Object.entries(dataToShow).map(([key, items]) => {
          const first = items[0];
          const count = items.length;

          return (
            <Marker
              key={key}
              position={[first.lat, first.lng]}
              icon={createCheckinIcon(count)}
              eventHandlers={{
                click: (e) => {
                  const map = e.target._map; // grab Leaflet map
                  if (zoomLevel === "continent") {
                    setZoomLevel("country");
                    map.flyTo([first.lat, first.lng], 4);
                  } else if (zoomLevel === "country") {
                    setZoomLevel("city");
                    map.flyTo([first.lat, first.lng], 7);
                  } else if (zoomLevel === "city") {
                    setZoomLevel("city");
                    map.flyTo([first.lat, first.lng], 12);
                    setCityConcerts(items);
                    setModalOpen(true);
                  }
                },
              }}
            />
          );
        })}


        {/* <ChangeMapView center={mapCenter} zoom={mapZoom} /> */}
      </MapContainer>

      <GalleryModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        concerts={cityConcerts}
      />
    </>
  );
}
