import React from "react";

export default function ConcertListPanel({ open, onClose, concerts, theater }) {
  return (
    <div className={`side-panel ${open ? "open" : ""}`}>
      <div className="panel-header">
        {theater && (
          <>
            {theater.photo && (
              <img src={theater.photo} alt={theater.name} className="theater-photo" />
            )}
            <h2>{theater.name}</h2>
          </>
        )}
        <button className="close-btn" onClick={onClose}>
          âœ•
        </button>
      </div>
      <div className="panel-content">
        {concerts.length === 0 ? (
          <p>No concerts found</p>
        ) : (
          concerts.map((c) => (
            <div key={c.id} className="concert-card">
              <div className="concert-date">
                {new Date(c.date).toLocaleDateString()}
              </div>
              <div className="concert-name">{c.name}</div>
              <div className="concert-artists">{c.artists?.join(", ")}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
