import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Simple modal
const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: "rgba(0,0,0,0.5)", zIndex: 1000, overflow: "auto"
    }}>
      <div style={{
        backgroundColor: "white", margin: "5% auto", padding: 20,
        width: "90%", maxWidth: 800, borderRadius: 8
      }}>
        <h2>{title}</h2>
        <div>{children}</div>
        <button onClick={onClose} style={{ marginTop: 10 }}>Close</button>
      </div>
    </div>
  );
};

// Custom check-in icon with number
const createCheckinIcon = (count) => {
  return L.divIcon({
    html: `<div style="
      position: relative;
      background-color: #1976d2;
      color: white;
      border-radius: 50%;
      width: 36px;
      height: 36px;
      display: flex;
      justify-content: center;
      align-items: center;
      font-weight: bold;
      font-size: 16px;
      border: 2px solid white;
    ">${count}</div>`,
    className: "",
    iconSize: [36, 36],
    iconAnchor: [18, 36],
    popupAnchor: [0, -36],
  });
};

// Aggregate concerts by zoom level and compute average lat/lng
const aggregateConcerts = (concerts, level) => {
  const grouped = {};
  concerts.forEach(c => {
    const key = c[level];
    if (!grouped[key]) grouped[key] = { latSum: 0, lngSum: 0, count: 0 };
    grouped[key].latSum += c.lat;
    grouped[key].lngSum += c.lng;
    grouped[key].count += 1;
  });

  // Compute average lat/lng
  Object.keys(grouped).forEach(k => {
    grouped[k].lat = grouped[k].latSum / grouped[k].count;
    grouped[k].lng = grouped[k].lngSum / grouped[k].count;
  });

  return grouped;
};

// Smoothly fly map to new center & zoom
const FlyMapView = ({ mapRef, center, zoom }) => {
  const map = mapRef.current;
  if (map) map.flyTo(center, zoom, { duration: 1.0 }); // 1s animation
  return null;
};

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent"); // continent | country | city
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(2);

  const mapRef = useRef(null);

  // Modal state
  const [modalOpen, setModalOpen] = useState(false);
  const [modalConcerts, setModalConcerts] = useState([]);
  const [modalTitle, setModalTitle] = useState("");

  // Load all concerts
  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then(res => res.json())
      .then(setConcerts)
      .catch(console.error);
  }, []);

  // Determine zoom level from map zoom
  const mapZoomHandler = (map) => {
    const zoom = map.getZoom();
    if (zoom < 3) setZoomLevel("continent");
    else if (zoom < 6) setZoomLevel("country");
    else setZoomLevel("city");
  };

  // Handle marker click
  const handleMarkerClick = async (key, firstItem) => {
    const nextLevel = zoomLevel === "continent" ? "country" : zoomLevel === "country" ? "city" : "city";
    const zoomMapping = { continent: 2, country: 4, city: 7 };

    // Smooth fly to next level
    if (mapRef.current) {
      mapRef.current.flyTo([firstItem.lat, firstItem.lng], zoomMapping[nextLevel], { duration: 1.0 });
    }

    // If not city level yet, just zoom in
    if (zoomLevel !== "city") {
      setZoomLevel(nextLevel);
      return; // do not show modal
    }

    // Only at city level, fetch concert details
    const res = await fetch(`http://localhost:8080/api/concerts/location?level=${zoomLevel}&key=${encodeURIComponent(key)}`);
    const data = await res.json();
    setModalConcerts(data);
    setModalTitle(key);
    setModalOpen(true);
  };

  const aggregated = aggregateConcerts(concerts, zoomLevel);

  return (
    <>
      <MapContainer
        center={mapCenter}
        zoom={mapZoom}
        style={{ height: "100vh", width: "100%" }}
        whenCreated={map => { 
          mapRef.current = map; 
          map.on("zoomend", () => mapZoomHandler(map)); 
        }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {Object.entries(aggregated).map(([key, val]) => (
          <Marker
            key={key}
            position={[val.lat, val.lng]}
            icon={createCheckinIcon(val.count)}
            eventHandlers={{ click: () => handleMarkerClick(key, val) }}
          />
        ))}

        <FlyMapView mapRef={mapRef} center={mapCenter} zoom={mapZoom} />
      </MapContainer>

      <Modal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        title={modalTitle}
      >
        {modalConcerts.map(c => (
          <div key={c.name + c.date} style={{ marginBottom: 20 }}>
            <strong>{c.name}</strong> ({c.date})<br/>
            Artists: {c.artists.join(", ")}<br/>
            Ticket: {c.ticket}<br/>
            {c.photos.map((p, i) => (
              <img key={i} src={p} alt={c.name} width={150} style={{ margin: 5 }} />
            ))}
            <hr/>
          </div>
        ))}
      </Modal>
    </>
  );
}
