import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, useMapEvents } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Simple modal
const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;
  return (
    <div style={{ position: "fixed", top:0,left:0,right:0,bottom:0, backgroundColor:"rgba(0,0,0,0.5)", zIndex:1000 }}>
      <div style={{backgroundColor:"white", margin:"5% auto", padding:20, width:"90%", maxWidth:800, borderRadius:8}}>
        <h2>{title}</h2>
        {children}
        <button onClick={onClose} style={{marginTop:10}}>Close</button>
      </div>
    </div>
  );
};

// Custom circular marker with count
const createCheckinIcon = (count) => L.divIcon({
  html: `<div style="position:relative;background-color:#1976d2;color:white;border-radius:50%;width:36px;height:36px;display:flex;justify-content:center;align-items:center;font-weight:bold;font-size:16px;border:2px solid white;">${count}</div>`,
  className:"",
  iconSize:[36,36],
  iconAnchor:[18,36],
  popupAnchor:[0,-36]
});

// Aggregate concerts by continent/country/city
const aggregateConcerts = (concerts, level) => {
  const grouped = {};
  concerts.forEach(c=>{
    const key = c[level];
    if(!grouped[key]) grouped[key]={latSum:0,lngSum:0,count:0,items:[]};
    grouped[key].latSum += c.lat;
    grouped[key].lngSum += c.lng;
    grouped[key].count += 1;
    grouped[key].items.push(c);
  });
  Object.keys(grouped).forEach(k=>{
    grouped[k].lat = grouped[k].latSum / grouped[k].count;
    grouped[k].lng = grouped[k].lngSum / grouped[k].count;
  });
  return grouped;
};

// Detect zoom changes
const ZoomHandler = ({ setZoomLevel }) => {
  useMapEvents({
    zoomend: (e) => {
      const zoom = e.target.getZoom();
      if (zoom < 3) setZoomLevel("continent");
      else if (zoom < 6) setZoomLevel("country");
      else setZoomLevel("city");
    }
  });
  return null;
};

export default function MapView(){
  const [concerts,setConcerts] = useState([]);
  const [zoomLevel,setZoomLevel] = useState("continent");
  const mapRef = useRef(null);

  const [modalOpen,setModalOpen] = useState(false);
  const [modalConcerts,setModalConcerts] = useState([]);
  const [modalTitle,setModalTitle] = useState("");

  const [flyToTarget,setFlyToTarget] = useState(null);

  // Fetch concerts from backend
  useEffect(()=>{
    fetch("http://localhost:8080/api/concerts")
      .then(res=>res.json())
      .then(setConcerts)
      .catch(console.error);
  },[]);

  // Fly to a target location when set
  useEffect(()=>{
    if(flyToTarget && mapRef.current){
      mapRef.current.flyTo([flyToTarget.lat, flyToTarget.lng], flyToTarget.zoom, {duration:1});
      setFlyToTarget(null);
    }
  },[flyToTarget]);

  // Handle marker click: drill-down or show modal
  const handleMarkerClick = (key, items) => {
    let nextLevel, zoom;
    if (zoomLevel === "continent") {
      nextLevel = "country";
      zoom = 4;
    } else if (zoomLevel === "country") {
      nextLevel = "city";
      zoom = 7;
    } else {
      nextLevel = "city"; // already at city
      zoom = 10;
    }

    if (zoomLevel !== "city") {
      setZoomLevel(nextLevel);
      setFlyToTarget({lat: items[0].lat, lng: items[0].lng, zoom});
    } else {
      setModalConcerts(items);
      setModalTitle(key);
      setModalOpen(true);
    }
  };

  // Aggregate concerts according to current zoom level
  const aggregated = aggregateConcerts(concerts, zoomLevel);

  return (
    <>
      <MapContainer center={[20,0]} zoom={2} style={{height:"100vh", width:"100%"}} whenCreated={map=>mapRef.current=map}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap contributors" />
        <ZoomHandler setZoomLevel={setZoomLevel} />
        {Object.entries(aggregated).map(([key,val])=>(
          <Marker
            key={key}
            position={[val.lat,val.lng]}
            icon={createCheckinIcon(val.count)}
            eventHandlers={{click:()=>handleMarkerClick(key,val.items)}}
          />
        ))}
      </MapContainer>

      <Modal isOpen={modalOpen} onClose={()=>setModalOpen(false)} title={modalTitle}>
        {modalConcerts.map(c=>(
          <div key={c.name+c.date} style={{marginBottom:20}}>
            <strong>{c.name}</strong> ({c.date})<br/>
            Artists: {c.artists.join(", ")}<br/>
            Ticket: {c.ticket}<br/>
            {c.photos.map((p,i)=>(
              <img key={i} src={p} alt={c.name} width={150} style={{margin:5}}/>
            ))}
            <hr/>
          </div>
        ))}
      </Modal>
    </>
  );
}
