import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix default icon path for React + Webpack
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png")
});

// Optional check-in icon
const checkinIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/190/190411.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32],
});

function groupByLevel(concerts, level) {
  const grouped = {};
  (concerts || []).forEach((c) => {
    const key = c[level];
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(c);
  });
  return grouped;
}

// Component to programmatically change map view
function ChangeMapView({ center, zoom }) {
  const map = useMap();
  map.setView(center, zoom);
  return null;
}

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent"); // continent | country | city
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(2);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const levelKey = zoomLevel;
  const dataToShow = groupByLevel(concerts, levelKey);

  // Determine next zoom level
  const nextZoomLevel = zoomLevel === "continent" ? "country" :
                        zoomLevel === "country" ? "city" :
                        "city";

  const zoomMapping = { continent: 2, country: 4, city: 7 };

  return (
    <MapContainer center={mapCenter} zoom={mapZoom} style={{ height: "100vh", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />

      {Object.entries(dataToShow).map(([key, items]) => {
        const first = items[0];

        return (
          <Marker
            key={key}
            position={[first.lat, first.lng]}
            icon={checkinIcon}
            eventHandlers={{
              click: () => {
                // Update zoom level
                if (zoomLevel !== "city") setZoomLevel(nextZoomLevel);

                // Center map on clicked marker
                setMapCenter([first.lat, first.lng]);
                setMapZoom(zoomMapping[nextZoomLevel]);
              },
            }}
          >
            <Popup>
              <h3>{key}</h3>
              <p>Total concerts: {items.length}</p>
              {items.map((c) => (
                <div key={c.name}>
                  <strong>{c.name}</strong> ({c.date})<br />
                  Artists: {c.artists.join(", ")}<br />
                  Ticket: {c.ticket}<br />
                  {c.photos.map((p, i) => (
                    <img key={i} src={p} alt={c.name} width={100} style={{ margin: "2px" }} />
                  ))}
                  <hr />
                </div>
              ))}
            </Popup>
          </Marker>
        );
      })}

      {/* Change map view on state change */}
      <ChangeMapView center={mapCenter} zoom={mapZoom} />
    </MapContainer>
  );
}
