import { useEffect, useState } from "react";
import { useMap, Marker } from "react-leaflet";
import L from "leaflet";

export function SyncZoom({ concerts, setZoomLevel, setMapZoom }) {
  const map = useMap();
  const [center, setCenter] = useState(null);

  useEffect(() => {
    if (!concerts || concerts.length === 0) return;

    const bounds = L.latLngBounds(concerts.map(c => [c.lat, c.lng]));

    // ✅ Fit all concerts
    map.fitBounds(bounds, { padding: [50, 50] });

    // ✅ Store the center so we can drop a pin there
    const boundsCenter = bounds.getCenter();
    console.log("Bounds center:", boundsCenter);
    setCenter(boundsCenter);

    const handleZoom = () => {
      const z = map.getZoom();
      setMapZoom(z);

      if (z < 3) setZoomLevel("continent");
      else if (z < 6) setZoomLevel("country");
      else if (z < 10) setZoomLevel("city");
      else setZoomLevel("city");
    };

    map.on("zoomend", handleZoom);
    handleZoom();

    return () => {
      map.off("zoomend", handleZoom);
    };
  }, [map, concerts, setZoomLevel, setMapZoom]);

  return center ? (
    <Marker position={center} />
  ) : null;
}
