import React, { useState, useEffect } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  useMap,
} from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix default icon path for React + Webpack
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

// Dynamic count-based icon
const createCheckinIcon = (count) =>
  L.divIcon({
    html: `<div style="
      position:relative;
      background-color:#1976d2;
      color:white;
      border-radius:50%;
      width:36px;
      height:36px;
      display:flex;
      justify-content:center;
      align-items:center;
      font-weight:bold;
      font-size:16px;
      border:2px solid white;
    ">${count}</div>`,
    className: "",
    iconSize: [36, 36],
    iconAnchor: [18, 36],
    popupAnchor: [0, -36],
  });

// Group concerts by continent, country, or city
function groupByLevel(concerts, level) {
  const grouped = {};
  (concerts || []).forEach((c) => {
    const key = c[level];
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(c);
  });
  return grouped;
}

// Component to update map view
function ChangeMapView({ center, zoom }) {
  const map = useMap();
  map.setView(center, zoom, { animate: true });
  return null;
}

// Hook: sync manual zoom events to zoomLevel
function SyncZoom({ setZoomLevel, setMapZoom }) {
  const map = useMap();

  useEffect(() => {
    const handleZoom = () => {
      const z = map.getZoom();
      setMapZoom(z);

      if (z < 3) setZoomLevel("continent");
      else if (z < 6) setZoomLevel("country");
      else if (z < 10) setZoomLevel("city");
      else setZoomLevel("city"); // keep city detail under "city"
    };

    map.on("zoomend", handleZoom);
    handleZoom(); // run once initially

    return () => {
      map.off("zoomend", handleZoom);
    };
  }, [map, setZoomLevel, setMapZoom]);

  return null;
}

// Simple Gallery Modal
function GalleryModal({ isOpen, onClose, concerts }) {
  const [index, setIndex] = useState(0);

  if (!isOpen || concerts.length === 0) return null;

  const current = concerts[index];

  const prev = () => setIndex((i) => (i > 0 ? i - 1 : concerts.length - 1));
  const next = () => setIndex((i) => (i < concerts.length - 1 ? i + 1 : 0));

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: "rgba(0,0,0,0.7)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 2000,
      }}
    >
      <div
        style={{
          background: "white",
          padding: 20,
          borderRadius: 8,
          width: "80%",
          maxWidth: 700,
          position: "relative",
        }}
      >
        <button
          onClick={onClose}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            background: "#f44336",
            color: "white",
            border: "none",
            borderRadius: "50%",
            width: 30,
            height: 30,
            cursor: "pointer",
          }}
        >
          ✖
        </button>

        <h2>{current.name}</h2>
        <p>
          {current.city}, {current.country} — {current.date}
        </p>
        <p>
          <strong>Artists:</strong> {current.artists.join(", ")}
        </p>
        <p>
          <strong>Ticket:</strong> {current.ticket}
        </p>

        <div
          style={{
            display: "flex",
            overflowX: "auto",
            gap: "10px",
            margin: "15px 0",
          }}
        >
          {current.photos.map((p, i) => (
            <img
              key={i}
              src={p}
              alt={current.name}
              style={{ maxHeight: "200px", borderRadius: 6 }}
            />
          ))}
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: 10,
          }}
        >
          <button onClick={prev}>⬅ Prev</button>
          <button onClick={next}>Next ➡</button>
        </div>
      </div>
    </div>
  );
}

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent"); // continent → country → city
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(2);

  const [modalOpen, setModalOpen] = useState(false);
  const [cityConcerts, setCityConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  const zoomMapping = { continent: 2, country: 4, city: 7, cityDetail: 12 };

  return (
    <>
      <MapContainer
        center={mapCenter}
        zoom={mapZoom}
        style={{ height: "100vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* NEW: auto adjust zoomLevel when user zooms manually */}
        <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />

        {Object.entries(dataToShow).map(([key, items]) => {
          const first = items[0];
          const count = items.length;

          return (
            <Marker
              key={key}
              position={[first.lat, first.lng]}
              icon={createCheckinIcon(count)}
              eventHandlers={{
                click: () => {
                  if (zoomLevel === "continent") {
                    setZoomLevel("country");
                    setMapCenter([first.lat, first.lng]);
                    setMapZoom(zoomMapping["country"]);
                  } else if (zoomLevel === "country") {
                    setZoomLevel("city");
                    setMapCenter([first.lat, first.lng]);
                    setMapZoom(zoomMapping["city"]);
                  } else if (zoomLevel === "city") {
                    setMapCenter([first.lat, first.lng]);
                    setMapZoom(zoomMapping["cityDetail"]);
                    setCityConcerts(items);
                    setModalOpen(true);
                  }
                },
              }}
            />
          );
        })}

        <ChangeMapView center={mapCenter} zoom={mapZoom} />
      </MapContainer>

      <GalleryModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        concerts={cityConcerts}
      />
    </>
  );
}
