import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import groupByLevel from "../utils/groupByLevel";
import { SyncZoom } from "./SyncZoom";
import { createCheckinIcon } from "../utils/leafletIcon";
import "./MapView.css"; // Add this for custom styles

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent");
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(3);

  const [theaterConcerts, setTheaterConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  // Panel open if theaterConcerts is set and zoomLevel is theaterDetails
  const panelOpen = zoomLevel === "theaterDetails" && theaterConcerts.length > 0;

  return (
    <div className={`mapview-container${panelOpen ? " panel-open" : ""}`}>
      {panelOpen && (
        <div className="left-panel">
          <h3>Concerts</h3>
          <ul>
            {theaterConcerts.map((concert) => (
              <li key={concert.id}>
                <strong>{concert.name}</strong>
                <br />
                {concert.date}
                <br />
                {concert.artist}
              </li>
            ))}
          </ul>
        </div>
      )}

      <MapContainer
        worldCopyJump={true}
        center={mapCenter}
        zoom={mapZoom}
        minZoom={3}
        maxZoom={18}
        maxBounds={[
          [-85, -180],
          [85, 180],
        ]}
        style={{ height: "100vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OSM</a>'
        />

        <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />

        {Object.entries(dataToShow).map(([key, items]) => {
          const first = items[0];
          const count = items.length;

          if (!first.theater || first.theater.lat == null || first.theater.lng == null) return null;

          return (
            <div className={`mapview-container${panelOpen ? " panel-open" : ""}`}>
              <div
                className={`concert-panel${panelOpen ? " open" : ""}`}
                style={{ pointerEvents: panelOpen ? "auto" : "none" }}
              >
                <h2>Concerts</h2>
                {theaterConcerts.map((concert) => (
                  <div className="concert-item" key={concert.id}>
                    <h3>{concert.name}</h3>
                    <div>{concert.date}</div>
                    <small>{concert.artist}</small>
                  </div>
                ))}
              </div>

              <div
                className={`map-container${panelOpen ? " shifted" : ""}`}
                onClick={() => {
                  if (panelOpen) {
                    setZoomLevel("theater");
                    setTheaterConcerts([]);
                  }
                }}
              >
                <MapContainer
                  worldCopyJump={true}
                  center={mapCenter}
                  zoom={mapZoom}
                  minZoom={3}
                  maxZoom={18}
                  maxBounds={[
                    [-85, -180],
                    [85, 180],
                  ]}
                  style={{ height: "100vh", width: "100%" }}
                >
                  {/* ...existing code... */}
                </MapContainer>
              </div>
            </div>
          );
        })}
      </MapContainer>
    </div>
  );
}
