import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from "react-leaflet";
import L from "leaflet";

// Optional: custom check-in icon
const checkinIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/190/190411.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32],
});

function groupByLevel(concerts, level) {
  // safe: concerts defaults to empty array
  const grouped = {};
  (concerts || []).forEach((c) => {
    const key = c[level];
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(c);
  });
  return grouped;
}

export default function MapView() {
  const [concerts, setConcerts] = useState([]); // <-- important fix!
  const [zoomLevel, setZoomLevel] = useState("continent"); // continent | country | city
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch((err) => console.error(err));
  }, []);

  // determine current level key
  let levelKey = zoomLevel;
  let dataToShow = groupByLevel(concerts, levelKey);

  return (
    <MapContainer center={[20, 0]} zoom={2} style={{ height: "100vh", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />
      {Object.entries(dataToShow).map(([key, items]) => {
        const first = items[0];
        return (
          <Marker
            key={key}
            position={[first.lat, first.lng]}
            icon={checkinIcon}
            eventHandlers={{
              click: () => {
                if (zoomLevel === "continent") setZoomLevel("country");
                else if (zoomLevel === "country") setZoomLevel("city");
              },
            }}
          >
            <Popup>
              <h3>{key}</h3>
              <p>Total concerts: {items.length}</p>
              {items.map((c) => (
                <div key={c.name}>
                  <strong>{c.name}</strong> ({c.date})<br />
                  Artists: {c.artists.join(", ")}<br />
                  Ticket: {c.ticket}<br />
                  {c.photos.map((p, i) => (
                    <img key={i} src={p} alt={c.name} width={100} style={{ margin: "2px" }} />
                  ))}
                  <hr />
                </div>
              ))}
            </Popup>
          </Marker>
        );
      })}
    </MapContainer>
  );
}
