// SyncZoom.js
import { useEffect, useRef } from "react";
import { useMap } from "react-leaflet";
import L from "leaflet";

export function SyncZoom({ setZoomLevel, setMapZoom }) {
    const map = useMap();

    useEffect(() => {
        const handleZoom = () => {
            const z = map.getZoom();
            setMapZoom(z);

            if (z < 3) setZoomLevel("continent");
            else if (z < 6) setZoomLevel("country");
            else if (z < 10) setZoomLevel("city");
            else setZoomLevel("city");
        };

        map.on("zoomend", handleZoom);
        handleZoom();

        return () => {
            map.off("zoomend", handleZoom);
        };
    }, [map, setZoomLevel, setMapZoom]);

    return null;
}

export function FitToDataBounds({ concerts }) {
    const map = useMap();
    const fittedOnce = useRef(false);

    useEffect(() => {
        if (!fittedOnce.current && concerts && concerts.length > 0) {
            const bounds = L.latLngBounds(concerts.map(c => [c.lat, c.lng]));

            const rect = L.rectangle(bounds, {
                color: "red",
                weight: 2,
                dashArray: "5,5",
                fillOpacity: 0,
            }).addTo(map);

            map.fitBounds(bounds, { padding: [50, 50] });
            fittedOnce.current = true; // ✅ only fit the first time
        }
    }, [concerts, map]);

    return null;
}
