import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import groupByLevel from "../utils/groupByLevel";
import ChangeMapView from "./ChangeMapView";
import { SyncZoom, FitToClusters } from "./SyncZoom";
import ConcertMarker from "./ConcertMarker";
import GalleryModal from "./GalleryModal";

export default function MapView() {
  const [concerts, setConcerts] = useState([]);
  const [zoomLevel, setZoomLevel] = useState("continent");
  const [mapCenter, setMapCenter] = useState([20, 0]);
  const [mapZoom, setMapZoom] = useState(3);

  const [modalOpen, setModalOpen] = useState(false);
  const [cityConcerts, setCityConcerts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/concerts")
      .then((res) => res.json())
      .then((data) => setConcerts(data))
      .catch(console.error);
  }, []);

  const dataToShow = groupByLevel(concerts, zoomLevel);

  return (
    <>
      <MapContainer worldCopyJump={true} center={mapCenter} zoom={mapZoom} style={{ height: "100vh", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        <SyncZoom setZoomLevel={setZoomLevel} setMapZoom={setMapZoom} />
        <MarkerClusterGroup ref={clusterGroupRef}>
          {concerts.map(concert => (
            <Marker key={concert.id} position={[concert.lat, concert.lng]} />
          ))}
        </MarkerClusterGroup>

        <FitToClusters clusterGroupRef={clusterGroupRef} />


        {Object.entries(dataToShow).map(([key, items]) => (
          <ConcertMarker
            key={key}
            groupKey={key}
            items={items}
            zoomLevel={zoomLevel}
            setZoomLevel={setZoomLevel}
            setMapCenter={setMapCenter}
            setMapZoom={setMapZoom}
            setModalOpen={setModalOpen}
            setCityConcerts={setCityConcerts}
          />
        ))}

        <ChangeMapView center={mapCenter} zoom={mapZoom} />
      </MapContainer>

      <GalleryModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        concerts={cityConcerts}
      />
    </>
  );
}
